import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import axios from "axios";
import { authenticateAdmin } from "./Auth";

const AdminPanel = ({ fetchItems, onAdminStatusChange }) => {
  const [isAdminModalOpen, setIsAdminModalOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem("isAuthenticated") === "true"
  );
  const [isAddProductModalOpen, setIsAddProductModalOpen] = useState(false);
  const [isAddTransactionModalOpen, setIsAddTransactionModalOpen] =
    useState(false);
  const [newProduct, setNewProduct] = useState({
    nume_produs: "",
    um: "",
    tva: "",
    pret: "",
  });
  const [transactionProduct, setTransactionProduct] = useState({
    produs_id: "",
    cantitate: "",
  });

  useEffect(() => {
    if (isAuthenticated) {
      localStorage.setItem("isAuthenticated", "true");
    } else {
      localStorage.removeItem("isAuthenticated");
    }

    // Set up WebSocket connection
    const ws = new WebSocket("ws://localhost:3000");

    ws.onopen = () => {
      console.log("WebSocket connection opened");
    };

    ws.onmessage = (message) => {
      const data = JSON.parse(message.data);
      console.log("Received WebSocket message:", data);
      if (data.items) {
        fetchItems(); // Refresh items when WebSocket message is received
      }
    };

    ws.onclose = () => {
      console.log("WebSocket connection closed");
    };

    // Cleanup WebSocket connection when the component unmounts
    return () => {
      ws.close();
    };
  }, [isAuthenticated, fetchItems]);

  // Handle admin authentication
  const handleAdminLogin = (username, password) => {
    if (authenticateAdmin(username, password)) {
      setIsAuthenticated(true);
      localStorage.setItem("isAuthenticated", "true");
      setIsAdminModalOpen(false);
      alert("Login successful");
      onAdminStatusChange(true); // Notify Home component of admin login
    } else {
      alert("Invalid credentials");
    }
  };

  // Handle adding a new product to `Produse` (stock) table
  const handleAddProductToStock = async () => {
    try {
      const response = await axios.post(
        "http://localhost:3000/admin/add-stock",
        newProduct
      );
      console.log("Server response:", response.data);
      setNewProduct({ nume_produs: "", um: "", tva: "", pret: "" });
      setIsAddProductModalOpen(false);
      alert("Product added to stock successfully");
      fetchItems(); // Refresh items
    } catch (error) {
      console.error("Error adding product to stock:", error);
      alert("Failed to add product to stock");
    }
  };

  // Handle adding a new product to `Tranzactii` table
  const handleAddProductToTransactions = async () => {
    try {
      await axios.post("http://localhost:3000/add-product", transactionProduct);
      setTransactionProduct({ produs_id: "", cantitate: "" });
      setIsAddTransactionModalOpen(false);
      alert("Product added to transactions successfully");
      fetchItems(); // Refresh items
    } catch (error) {
      alert("Failed to add product to transactions");
    }
  };

  // Handle admin logout
  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("isAuthenticated");
    alert("Logged out successfully");
    onAdminStatusChange(false); // Notify Home component of admin logout
  };

  return (
    <div>
      {!isAuthenticated ? (
        <button
          onClick={() => setIsAdminModalOpen(true)}
          className="admin-button"
        >
          Autentificare Administrare
        </button>
      ) : (
        <>
          <button
            onClick={() => setIsAddProductModalOpen(true)}
            className="admin-button"
          >
            Adaugare Produs in stoc
          </button>
          <button
            onClick={() => setIsAddTransactionModalOpen(true)}
            className="admin-button"
          >
            Adaugare Produs
          </button>
          <button onClick={handleLogout} className="admin-button">
            Logout
          </button>
        </>
      )}
      {/* Admin Authentication Modal */}
      <Modal
        isOpen={isAdminModalOpen}
        onRequestClose={() => setIsAdminModalOpen(false)}
        contentLabel="Admin Login"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <AdminLoginForm onLogin={handleAdminLogin} />
      </Modal>
      {/* Add Product to Stock Modal */}
      <Modal
        isOpen={isAddProductModalOpen}
        onRequestClose={() => setIsAddProductModalOpen(false)}
        contentLabel="Add Product to Stock"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <AdminAddProductForm
          newProduct={newProduct}
          setNewProduct={setNewProduct}
          onSubmit={handleAddProductToStock}
        />
      </Modal>
      {/* Add Product to Transactions Modal */}
      <Modal
        isOpen={isAddTransactionModalOpen}
        onRequestClose={() => setIsAddTransactionModalOpen(false)}
        contentLabel="Add Product to Transactions"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <AdminAddTransactionForm
          transactionProduct={transactionProduct}
          setTransactionProduct={setTransactionProduct}
          onSubmit={handleAddProductToTransactions}
        />
      </Modal>
    </div>
  );
};

const AdminLoginForm = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Autentificare Administrare</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit">Login</button>
    </form>
  );
};

const AdminAddProductForm = ({ newProduct, setNewProduct, onSubmit }) => {
  return (
    <form onSubmit={onSubmit}>
      <h2>Add New Product to Stock</h2>
      <input
        type="text"
        placeholder="Product Name"
        value={newProduct.nume_produs}
        onChange={(e) =>
          setNewProduct({ ...newProduct, nume_produs: e.target.value })
        }
        required
      />
      <input
        type="text"
        placeholder="Unit of Measure (e.g., kg)"
        value={newProduct.um}
        onChange={(e) => setNewProduct({ ...newProduct, um: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="VAT"
        value={newProduct.tva}
        onChange={(e) => setNewProduct({ ...newProduct, tva: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Price"
        value={newProduct.pret}
        onChange={(e) => setNewProduct({ ...newProduct, pret: e.target.value })}
        required
      />
      <button type="submit">Adaugare produs in stoc</button>
    </form>
  );
};

const AdminAddTransactionForm = ({
  transactionProduct,
  setTransactionProduct,
  onSubmit,
}) => {
  return (
    <form onSubmit={onSubmit}>
      <h2>Add Product to Transactions</h2>
      <input
        type="number"
        placeholder="Product ID"
        value={transactionProduct.produs_id}
        onChange={(e) =>
          setTransactionProduct({
            ...transactionProduct,
            produs_id: e.target.value,
          })
        }
        required
      />
      <input
        type="number"
        placeholder="Quantity"
        value={transactionProduct.cantitate}
        onChange={(e) =>
          setTransactionProduct({
            ...transactionProduct,
            cantitate: e.target.value,
          })
        }
        required
      />
      <button type="submit">Adaugare produs</button>
    </form>
  );
};

export default AdminPanel;
