import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
import logo from "./logo.png";
import shoppingCartGif from "./shopping-cart.gif";
import CheckoutForm from "./CheckoutForm";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import Modal from "react-modal";
import AdminPanel from "./AdminPanel";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Hardcode the Stripe public key directly here
const stripePromise = loadStripe(
  "pk_test_51Ppxn9C03kdICvcM1kvsmqvLZ41GmpbtuTehGZIT5EH5JXCxcT2BMYKefmkYvUJ0Ia0VhLseNywl5exjKrB9Ng5Y00aDbrTXCa"
);

const Home = () => {
  const [items, setItems] = useState([]);
  const [clientSecret, setClientSecret] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [totalAmount, setTotalAmount] = useState(0);
  const [receiptId, setReceiptId] = useState(null);
  const [isAdmin, setIsAdmin] = useState(
    localStorage.getItem("isAuthenticated") === "true"
  );

  // Function to fetch items
  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "http://localhost:3000/api/detected-items"
      );
      setItems(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      toast.error("Error fetching items.");
      setItems([]);
    }
  };

  // Handle checkout and create PaymentIntent
  const handleCheckout = async () => {
    try {
      const amount = Math.round(
        items.reduce((total, item) => total + item.pret * item.cantitate, 0) *
          100
      );

      setTotalAmount(amount / 100);

      const response = await axios.post(
        "http://localhost:3000/create-payment-intent",
        {
          amount,
        }
      );

      setClientSecret(response.data.clientSecret);
      setIsModalOpen(true);
    } catch (error) {
      toast.error("Error creating payment intent.");
    }
  };

  // Handle delete product
  const handleDelete = async (produs_id) => {
    if (!isAdmin) {
      toast.error("Access denied. Admins only.");
      return;
    }

    try {
      await axios.delete(`http://localhost:3000/delete-product/${produs_id}`);
      fetchItems(); // Refresh the items after deletion
      toast.success("Product deleted successfully");
    } catch (error) {
      toast.error("Failed to delete product.");
    }
  };

  // Close modal handler
  const closeModal = () => {
    setIsModalOpen(false);
    setReceiptId(null);
  };

  useEffect(() => {
    fetchItems(); // Fetch initial data

    // Set up WebSocket connection
    const ws = new WebSocket("ws://localhost:3000");

    ws.onopen = () => {
      console.log("WebSocket connection opened");
    };

    ws.onmessage = (message) => {
      const data = JSON.parse(message.data);
      console.log("Received WebSocket message:", data);
      if (data.items) {
        setItems(data.items); // Update the items state when new data is received
        console.log("Items after update:", data.items); // Log items array
      }
    };

    ws.onclose = () => {
      console.log("WebSocket connection closed");
    };

    // Cleanup WebSocket connection when the component unmounts
    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="home-container">
      <ToastContainer /> {/* Toast container for notifications */}
      <div className="top-section">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      {items.length === 0 ? (
        <div>
          <p>Plasati produsele pe cantar</p>
          <img
            src={shoppingCartGif}
            alt="Shopping Cart"
            className="loading-gif"
          />
        </div>
      ) : (
        <>
          <table className="items-table">
            <thead>
              <tr>
                <th>Nr. crt.</th>
                <th>Denumire</th>
                <th>Cantitate</th>
                <th>Pret</th>
                {isAdmin && <th>Action</th>}{" "}
                {/* Show delete button only for admin */}
              </tr>
            </thead>
            <tbody>
              {Array.isArray(items) && items.length > 0 ? (
                items.map((item, index) => (
                  <tr key={item.produs_id}>
                    <td>{index + 1}</td>
                    <td>{item.nume_produs}</td>
                    <td>{item.cantitate}</td>
                    <td>
                      {(item.um === "kg"
                        ? item.cantitate / 1000
                        : item.cantitate) * item.pret}{" "}
                      lei
                    </td>
                    {isAdmin && (
                      <td>
                        <button onClick={() => handleDelete(item.produs_id)}>
                          Delete
                        </button>
                      </td>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5">No items found</td>{" "}
                  {/* Increased colspan to account for delete column */}
                </tr>
              )}
            </tbody>
          </table>

          <div className="total-price">
            <h3>
              Total: RON{" "}
              {items
                .reduce(
                  (total, item) =>
                    total +
                    (item.um === "kg"
                      ? item.cantitate / 1000
                      : item.cantitate) *
                      item.pret,
                  0
                )
                .toFixed(2)}
            </h3>
          </div>

          <button onClick={handleCheckout} className="checkout-button">
            Continuati catre plata
          </button>
        </>
      )}
      {/* AdminPanel Component */}
      <AdminPanel fetchItems={fetchItems} />{" "}
      {/* Pass fetchItems to refresh data */}
      {/* Payment Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Payment Modal"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        {receiptId ? (
          <>
            <h2>Plata a fost efectuata cu succes!</h2>
            <p>Total platit: {totalAmount.toFixed(2)} lei</p>
            <p>ID Bon: {receiptId}</p>
            <button onClick={closeModal} className="close-button">
              Inchide
            </button>
          </>
        ) : (
          <>
            <h2>Finalizati plata</h2>
            <p>Total de plata: {totalAmount.toFixed(2)} lei</p>
            {clientSecret && (
              <Elements stripe={stripePromise}>
                <CheckoutForm
                  clientSecret={clientSecret}
                  setItems={setItems}
                  setIsModalOpen={setIsModalOpen}
                  setReceiptId={setReceiptId}
                />
              </Elements>
            )}
            <button onClick={closeModal} className="close-button">
              Anulare
            </button>
          </>
        )}
      </Modal>
    </div>
  );
};

export default Home;
