import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
import logo from "./logo.png";
import shoppingCartGif from "./shopping-cart.gif";
import CheckoutForm from "./CheckoutForm";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import Modal from "react-modal";
import AdminPanel from "./AdminPanel";

// Cheie publica STRIPE
const stripePromise = loadStripe(
  "pk_test_51Ppxn9C03kdICvcM1kvsmqvLZ41GmpbtuTehGZIT5EH5JXCxcT2BMYKefmkYvUJ0Ia0VhLseNywl5exjKrB9Ng5Y00aDbrTXCa"
);

const Acasa = () => {
  const [produse, seteazaProduse] = useState([]);
  const [stripeClientSecret, setStripeClientSecret] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [sumaTotala, setSumaTotala] = useState(0);
  const [idBon, setIdBon] = useState(null);
  const [isAdmin, setIsAdmin] = useState(
    localStorage.getItem("isAuthenticated") === "true"
  );

  // Functie pentru a obtine produsele
  const obtineProduse = async () => {
    try {
      const response = await axios.get(
        "http://localhost:3000/api/detected-items"
      );
      seteazaProduse(Array.isArray(response.data) ? response.data : []);
    } catch (eroare) {
      alert("Eroare la obținerea produselor.");
      seteazaProduse([]);
    }
  };

  // Gestioneaza finalizarea cumparaturilor
  const proceseazaPlata = async () => {
    try {
      const suma = Math.round(
        produse.reduce(
          (total, produs) => total + produs.pret * produs.cantitate,
          0
        ) * 100
      );

      setSumaTotala(suma / 100);

      const response = await axios.post(
        "http://localhost:3000/create-payment-intent",
        {
          suma,
        }
      );

      setStripeClientSecret(response.data.clientSecret);
      setIsModalOpen(true);
    } catch (eroare) {
      alert("Eroare la procesarea platii.");
    }
  };

  // Gestionează ștergerea produsului
  const gestioneazaStergerea = async (produs_id) => {
    if (!isAdmin) {
      alert("Acces interzis. Doar pentru administratori.");
      return;
    }

    try {
      await axios.delete(`http://localhost:3000/delete-product/${produs_id}`);
      obtineProduse(); // Reîmprospătează produsele după ștergere
      alert("Produs șters cu succes");
    } catch (eroare) {
      alert("Eroare la ștergerea produsului.");
    }
  };

  // Funcție pentru a închide modalul
  const inchideModal = () => {
    setIsModalOpen(false);
    setIdBon(null);
  };

  // Gestionează schimbarea statutului de admin (autentificare/deconectare)
  const gestioneazaSchimbareStatutAdmin = (statut) => {
    setIsAdmin(statut);
  };

  useEffect(() => {
    obtineProduse(); // Obține datele inițiale

    // Configurare conexiune WebSocket
    const ws = new WebSocket("ws://localhost:3000");

    ws.onopen = () => {
      console.log("Conexiune WebSocket deschisă");
    };

    ws.onmessage = (mesaj) => {
      const date = JSON.parse(mesaj.data);
      console.log("Mesaj WebSocket primit:", date);
      if (date.items) {
        seteazaProduse(date.items); // Actualizează starea produselor când sunt primite date noi
        console.log("Produse după actualizare:", date.items); // Loghează array-ul de produse
      }
    };

    ws.onclose = () => {
      console.log("Conexiune WebSocket închisă");
    };

    // Curățarea conexiunii WebSocket când componenta este demontată
    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="home-container">
      <div className="top-section">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      {produse.length === 0 ? (
        <div>
          <p>Plasați produsele pe cântar</p>
          <img
            src={shoppingCartGif}
            alt="Coș de cumpărături"
            className="loading-gif"
          />
        </div>
      ) : (
        <>
          <table className="items-table">
            <thead>
              <tr>
                <th>Nr. crt.</th>
                <th>Denumire</th>
                <th>Cantitate</th>
                <th>Preț</th>
                {isAdmin && <th>Acțiune</th>}{" "}
                {/* Afișează butonul de ștergere doar pentru admin */}
              </tr>
            </thead>
            <tbody>
              {Array.isArray(produse) && produse.length > 0 ? (
                produse.map((produs, index) => (
                  <tr key={produs.produs_id}>
                    <td>{index + 1}</td>
                    <td>{produs.nume_produs}</td>
                    <td>{produs.cantitate}</td>
                    <td>
                      {(produs.um === "kg"
                        ? produs.cantitate / 1000
                        : produs.cantitate) * produs.pret}{" "}
                      lei
                    </td>
                    {isAdmin && (
                      <td>
                        <button
                          onClick={() => gestioneazaStergerea(produs.produs_id)}
                        >
                          Șterge
                        </button>
                      </td>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5">Nu au fost găsite produse</td>{" "}
                  {/* Mărește colspan-ul pentru a ține cont de coloana ștergere */}
                </tr>
              )}
            </tbody>
          </table>

          <div className="total-price">
            <h3>
              Total: RON{" "}
              {produse
                .reduce(
                  (total, produs) =>
                    total +
                    (produs.um === "kg"
                      ? produs.cantitate / 1000
                      : produs.cantitate) *
                      produs.pret,
                  0
                )
                .toFixed(2)}
            </h3>
          </div>

          <button onClick={proceseazaPlata} className="checkout-button">
            Continuați către plată
          </button>
        </>
      )}
      {/* Componenta AdminPanel */}
      <AdminPanel
        fetchItems={obtineProduse}
        onAdminStatusChange={gestioneazaSchimbareStatutAdmin}
      />{" "}
      {/* Transmite obtineProduse și callback-ul pentru statutul admin */}
      {/* Modal de plată */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={inchideModal}
        contentLabel="Modal de plată"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        {idBon ? (
          <>
            <h2>Plata a fost efectuată cu succes!</h2>
            <p>Total plătit: {sumaTotala.toFixed(2)} lei</p>
            <p>ID Bon: {idBon}</p>
            <button onClick={inchideModal} className="close-button">
              Închide
            </button>
          </>
        ) : (
          <>
            <h2>Finalizați plata</h2>
            <p>Total de plată: {sumaTotala.toFixed(2)} lei</p>
            {stripeClientSecret && (
              <Elements stripe={stripePromise}>
                <CheckoutForm
                  clientSecret={stripeClientSecret}
                  setItems={seteazaProduse}
                  setIsModalOpen={setIsModalOpen}
                  setReceiptId={setIdBon}
                />
              </Elements>
            )}
            <button onClick={inchideModal} className="close-button">
              Anulare
            </button>
          </>
        )}
      </Modal>
    </div>
  );
};

export default Acasa;
