import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import axios from "axios";
import { credentialeAdmin } from "./Auth";

const AdminPanel = ({ obtineProduse, modificaStatutAdmin }) => {
  const [isAdminModalOpen, setIsAdminModalOpen] = useState(false);
  const [esteAutentificat, seteazaEsteAutentificat] = useState(
    localStorage.getItem("isAuthenticated") === "true"
  );
  const [esteModalAdaugaProdusDeschis, seteazaEsteModalAdaugaProdusDeschis] =
    useState(false);
  const [
    esteModalAdaugaTranzactieDeschis,
    seteazaEsteModalAdaugaTranzactieDeschis,
  ] = useState(false);
  const [produsNou, seteazaProdusNou] = useState({
    nume_produs: "",
    um: "",
    tva: "",
    pret: "",
  });
  const [produsTranzactie, seteazaProdusTranzactie] = useState({
    produs_id: "",
    cantitate: "",
  });

  useEffect(() => {
    if (esteAutentificat) {
      localStorage.setItem("isAuthenticated", "true");
    } else {
      localStorage.removeItem("isAuthenticated");
    }

    // Configurare conexiune WebSocket
    const ws = new WebSocket("ws://localhost:3000");

    ws.onopen = () => {
      console.log("Conexiune WebSocket deschisă");
    };

    ws.onmessage = (mesaj) => {
      const date = JSON.parse(mesaj.data);
      console.log("Mesaj WebSocket primit:", date);
      if (date.items) {
        obtineProduse(); // Reîmprospătează produsele când este primit mesajul WebSocket
      }
    };

    ws.onclose = () => {
      console.log("Conexiune WebSocket închisă");
    };

    // Curățare conexiune WebSocket la demontarea componentei
    return () => {
      ws.close();
    };
  }, [esteAutentificat, obtineProduse]);

  // Gestionează autentificarea administratorului
  const gestioneazaAutentificareAdmin = (username, parola) => {
    if (credentialeAdmin(username, parola)) {
      seteazaEsteAutentificat(true);
      localStorage.setItem("isAuthenticated", "true");
      setIsAdminModalOpen(false);
      alert("Autentificare reușită");
      modificaStatutAdmin(true); // Notifică componenta Home despre autentificarea adminului
    } else {
      alert("Credențiale invalide");
    }
  };

  // Gestionează adăugarea unui produs nou în tabelul `Produse` (stoc)
  const gestioneazaAdaugaProdusInStoc = async () => {
    try {
      const raspuns = await axios.post(
        "http://localhost:3000/admin/add-stock",
        produsNou
      );
      console.log("Răspuns server:", raspuns.data);
      seteazaProdusNou({ nume_produs: "", um: "", tva: "", pret: "" });
      seteazaEsteModalAdaugaProdusDeschis(false);
      alert("Produs adăugat în stoc cu succes");
      obtineProduse(); // Reîmprospătează produsele
    } catch (eroare) {
      console.error("Eroare la adăugarea produsului în stoc:", eroare);
      alert("Eșec la adăugarea produsului în stoc");
    }
  };

  // Gestionează adăugarea unui produs nou în tabelul `Tranzactii`
  const gestioneazaAdaugaProdusInTranzactii = async () => {
    try {
      await axios.post("http://localhost:3000/add-product", produsTranzactie);
      seteazaProdusTranzactie({ produs_id: "", cantitate: "" });
      seteazaEsteModalAdaugaTranzactieDeschis(false);
      alert("Produs adăugat în tranzacții cu succes");
      obtineProduse(); // Reîmprospătează produsele
    } catch (eroare) {
      alert("Eșec la adăugarea produsului în tranzacții");
    }
  };

  // Gestionează deconectarea administratorului
  const gestioneazaDeconectare = () => {
    seteazaEsteAutentificat(false);
    localStorage.removeItem("isAuthenticated");
    alert("Deconectare reușită");
    modificaStatutAdmin(false); // Notifică componenta Home despre deconectarea adminului
  };

  return (
    <div>
      {!esteAutentificat ? (
        <button
          onClick={() => setIsAdminModalOpen(true)}
          className="admin-button"
        >
          Autentificare Administrare
        </button>
      ) : (
        <>
          <button
            onClick={() => seteazaEsteModalAdaugaProdusDeschis(true)}
            className="admin-button"
          >
            Adăugare Produs în stoc
          </button>
          <button
            onClick={() => seteazaEsteModalAdaugaTranzactieDeschis(true)}
            className="admin-button"
          >
            Adăugare Produs
          </button>
          <button onClick={gestioneazaDeconectare} className="admin-button">
            Deconectare
          </button>
        </>
      )}
      {/* Modal de autentificare admin */}
      <Modal
        isOpen={isAdminModalOpen}
        onRequestClose={() => setIsAdminModalOpen(false)}
        contentLabel="Autentificare Admin"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <FormularAutentificareAdmin onLogin={gestioneazaAutentificareAdmin} />
      </Modal>
      {/* Modal adăugare produs în stoc */}
      <Modal
        isOpen={esteModalAdaugaProdusDeschis}
        onRequestClose={() => seteazaEsteModalAdaugaProdusDeschis(false)}
        contentLabel="Adăugare Produs în Stoc"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <FormularAdaugaProdus
          produsNou={produsNou}
          seteazaProdusNou={seteazaProdusNou}
          onSubmit={gestioneazaAdaugaProdusInStoc}
        />
      </Modal>
      {/* Modal adăugare produs în tranzacții */}
      <Modal
        isOpen={esteModalAdaugaTranzactieDeschis}
        onRequestClose={() => seteazaEsteModalAdaugaTranzactieDeschis(false)}
        contentLabel="Adăugare Produs în Tranzacții"
        ariaHideApp={false}
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        <FormularAdaugaTranzactie
          produsTranzactie={produsTranzactie}
          seteazaProdusTranzactie={seteazaProdusTranzactie}
          onSubmit={gestioneazaAdaugaProdusInTranzactii}
        />
      </Modal>
    </div>
  );
};

const FormularAutentificareAdmin = ({ onLogin }) => {
  const [username, seteazaUsername] = useState("");
  const [parola, seteazaParola] = useState("");

  const gestioneazaSubmit = (e) => {
    e.preventDefault();
    onLogin(username, parola);
  };

  return (
    <form onSubmit={gestioneazaSubmit}>
      <h2>Autentificare Administrare</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => seteazaUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Parola"
        value={parola}
        onChange={(e) => seteazaParola(e.target.value)}
        required
      />
      <button type="submit">Autentificare</button>
    </form>
  );
};

const FormularAdaugaProdus = ({ produsNou, seteazaProdusNou, onSubmit }) => {
  return (
    <form onSubmit={onSubmit}>
      <h2>Adăugare Produs Nou în Stoc</h2>
      <input
        type="text"
        placeholder="Nume Produs"
        value={produsNou.nume_produs}
        onChange={(e) =>
          seteazaProdusNou({ ...produsNou, nume_produs: e.target.value })
        }
        required
      />
      <input
        type="text"
        placeholder="Unitate de Măsură (ex: kg)"
        value={produsNou.um}
        onChange={(e) => seteazaProdusNou({ ...produsNou, um: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="TVA"
        value={produsNou.tva}
        onChange={(e) =>
          seteazaProdusNou({ ...produsNou, tva: e.target.value })
        }
        required
      />
      <input
        type="number"
        placeholder="Preț"
        value={produsNou.pret}
        onChange={(e) =>
          seteazaProdusNou({ ...produsNou, pret: e.target.value })
        }
        required
      />
      <button type="submit">Adăugare produs în stoc</button>
    </form>
  );
};

const FormularAdaugaTranzactie = ({
  produsTranzactie,
  seteazaProdusTranzactie,
  onSubmit,
}) => {
  return (
    <form onSubmit={onSubmit}>
      <h2>Adăugare Produs în Tranzacții</h2>
      <input
        type="number"
        placeholder="ID Produs"
        value={produsTranzactie.produs_id}
        onChange={(e) =>
          seteazaProdusTranzactie({
            ...produsTranzactie,
            produs_id: e.target.value,
          })
        }
        required
      />
      <input
        type="number"
        placeholder="Cantitate"
        value={produsTranzactie.cantitate}
        onChange={(e) =>
          seteazaProdusTranzactie({
            ...produsTranzactie,
            cantitate: e.target.value,
          })
        }
        required
      />
      <button type="submit">Adăugare produs</button>
    </form>
  );
};

export default AdminPanel;
