import React from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const CheckoutForm = ({ clientSecret, sessionId, setItems }) => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const cardElement = elements.getElement(CardElement);

    // Confirm the payment with Stripe
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      {
        payment_method: {
          card: cardElement,
        },
      }
    );

    if (error) {
      console.error("Payment error:", error);
    } else if (paymentIntent && paymentIntent.status === "succeeded") {
      console.log("Payment successful!");
      alert("Payment successful!");

      // Complete the transaction after payment
      try {
        const response = await axios.post(
          "http://localhost:3000/complete-transaction",
          {
            session_id: sessionId,
          }
        );
        // Clear items from the frontend after successful transaction
        setItems([]);
        console.log("Transaction completed:", response.data);
      } catch (transactionError) {
        console.error("Error completing transaction:", transactionError);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pay
      </button>
    </form>
  );
};

export default CheckoutForm;
